mapper.select:
SELECT JOIN_NO, JOIN_NAME, JOIN_PHONE, JOIN_CALSS, JOIN_TEXT, JOIN_DATE, ALLOW_STATUS
FROM JOIN
WHERE JOIN_NO = #{joinNo}

mapper.update:
UPDATE JOIN
SET
ALLOW_STATUS = #{allowStatus}
WHERE MEMBER_NO = #{memberNo} AND JOIN_NO = #{joinNo}

mapper.select:
SELECT COUNT(*)
FROM MEMBER M, JOIN J
WHERE M.MEMBER_NO = #{joinNo}
OR J.JOIN_NO = #{joinNo}
