mapper.update:
UPDATE QNA
SET QNA_TITLE = #{qnaTitle}, 
QNA_CONTENT = #{qnaContent}
WHERE QNA_NO = #{qnaNo}

mapper.select:
SELECT FILE_NO, SUBSTR(FILE_NAME, 50) AS FILE_NAME, FILE_NAME AS FILE_LOCATION, QNA_NO
FROM QNA_FILE
WHERE QNA_NO = #{qnaNo}

mapper.update:
UPDATE QNA SET
QNA_VIEW = QNA_VIEW+1
WHERE QNA_NO = #{qnaNo}
