mapper.update:
UPDATE STUDY SET
STUDY_CLASS = #{studyClass},
STUDY_TITLE = #{studyTitle},
STUDY_CONTENT = #{studyContent}
WHERE STUDY_NO = #{studyNo}

mapper.select:
SELECT FILE_NO, SUBSTR(FILE_NAME, 50) AS FILE_NAME, FILE_NAME AS FILE_LOCATION, STUDY_NO
FROM STUDY_FILE
WHERE STUDY_NO = #{studyNo}

mapper.update:
UPDATE STUDY SET
STUDY_VIEW = STUDY_VIEW + 1
WHERE STUDY_NO = #{studyNo}
